import{
  createBrowserRouter,
  RouterProvider,
  Route,
  Outlet
} from "react-router-dom";

const Layout = () =>{
  return (
    <div className ="app">
    <Navbar/>
    <Outlet/>
    <Footer/>
    </div>


  )
}

const router = createBrowserRouter([
  {
    path:"/",
    element:<Home/>
  },
  {
    path:"/products/:id",
    element:<Products/>
  },
  {
    path:"/product/:id",
    element:<Product/>
  },
])


function App() {
  return (
    <div>
      <RouterProvider router={router} />
    </div>
  );
}

export default App;
